function showpopup(url) {
  window.open(url, "popup", "width=720,height=700,top=100,left=100,toolbar=no,menubar=yes,location=no, resizable=yes,scrollbars=yes,status=no");
}

Ext.ns('Curriki.registration')
var Registration = Curriki.registration;

Registration.mandatoryValueField = [];
Registration.addMandatoryValueField = function(blockID, fieldID) {
  var pos = Registration.mandatoryValueField.length; 
  Registration.mandatoryValueField[pos] = new Array(); 
  Registration.mandatoryValueField[pos][0] = fieldID; 
  Registration.mandatoryValueField[pos][1] = blockID; 
}

Registration.testValueError = function(fieldName) {       
  var el = $(fieldName); 
  if(!el) return false; 
  var elValue = el.value; 
  if(elValue.length == 0 || elValue == '-' || elValue == '---') {         
    return true; 
  }       
  return false; 
}

Registration.hightlightEl = function(elName) {       
  $(elName).addClassName('highlight'); 
}

Registration.checkEmail = function() {
  var url= "$xwiki.getURL('XWiki.CheckAccountCreation').replaceAll('http:','https:')";
  var pars = 'xpage=plain&email=' + $F('XWiki.XWikiUsers_0_email') + '&login=' + $F('xwikiname');
  var myAjax = new Ajax.Request(url, {
    method: 'get'
    ,parameters: pars
    ,onComplete: function(originalRequest) {
      var text = originalRequest.responseText;
      var res = eval('[' + text + '][0]');
      if(!res.email) {
        alert(_('joincurriki.email.alreadyUsed'));
        return;
      }
      if(!res.login) {
        alert(_('joincurriki.login.alreadyUsed'));
        return;
      }        
      $('register').onSubmit = ""; 
      $('register').submit(); 
    }
  }); 
}

Registration.validateRegisterForm = function() { 
  var hasError =  false; 
  var errorMissing = false;
  $$('.register_block.highlight').invoke('removeClassName', 'highlight');
  if($F($('xwikiname')).replace(/[A-Za-z0-9]/g, '') != '') {         
    hasError = true; 
    Registration.hightLightEl('register_block_xwikiname'); 
    alert(_('joincurriki.xwikiname.badchars')); 
  }

  if($F($('password')).indexOf(" ") >= 0) { 
    hasError = true;
    Registration.highlightEl('register_block_password');
    alert(_('joincurriki.password.noSpaces'));
  }
  if($F($('password')).length < 5) {         
    hasError = true; 
    Registration.hightlightEl('register_block_password');
    alert(_('joincurriki.password.tooShort'));
  } else {         
    if($F($('password2')) != $F($('password'))) {           
      hasError = true;
      Registration.highlightEl('register_block_password');
      Registration.highlightEl('register_block_password2');
      alert(_('joincurriki.password.noMatch'));
    }      
  }

  var emailStr = $F($('email')); 
  var atIndex = emailStr.indexOf('@'); 
  if((atIndex < 1) 
    || (emailStr.lastIndexOf(".") <= (atIndex+1)) 
    || (emailStr.length <= (emailStr.lastIndexOf(".") + 1)) 
    || (emailStr.lastIndexOf("@") != atIndex) 
    || (emailStr.replace(/^[a-zA-Z0-9_+=.-]+@([a-zA-Z0-9-]+[.])+([a-zA-Z]){2,4}$/, '') != '')) {         
    hasError = true; 
    Registration.hightlightEl('register_block_email');
    alert(_('joincurriki.email.invalidFormat')); 
  }  
  for(var i = 0; i < Registration.mandatoryValueField.length; i++) {         
    if(!Registration.mandatoryValueField[i]) continue; 
    if(Registration.testValueError(Registration.mandatoryValueField[i][0])) {           
      errorMissing = true;
      Registration.hightlightEl(Registration.mandatoryValueField[i][1]); 
    }       
  }
  if(errorMissing) {
    hasError = true; 
    alert(_('joincurriki.someDataMissing')); 
  } else if (!hasError) {         
    Registration.checkEmail(); 
  }       

  return false; 
}

