  height: 100%;
}
#body {
  overflow:hidden;
  min-width: 0px;
}
#body {
  width: auto !important;
}
#xwikimaincontainer {
  width: auto !important;
}
#xwikimaincontainerinner {
  width: auto !important;
}
.x-window {
  top: 0 !important;
  left: 0 !important;
  position: static !important;
  width: auto !important;
}
.ext-el-mask {
  display:none !important;
}
</style>
$context.setLinksQueryString("xpage=bookmarklet")
#if($context.action.startsWith("login"))
<script type="text/javascript">
Ext.onReady(function() {
  var qs = Ext.parseURIQuery(window.location.href);
  qs = Ext.parseURIQuery(qs['xredirect']);
  var topLocation = qs['url'];
  if (typeof topLocation != "undefined" && topLocation != ""  && topLocation != "undefined") { 
    var width = Ext.get("body").getComputedWidth();
    var height = Ext.get("body").getComputedHeight(); 
    var heightHash = "height=" + height;
    var widthHash = "width=" + width;
    window.top.location = unescape(topLocation) + '#=' + heightHash + '&' + widthHash;
  }
});
</script>
#template("logininline.vm")
#else
  #template("contentview.vm")
#end
$context.unsetLinksQueryString()
<div class="clearfloats"></div>
</body>
</html>

